import { CycleType, CYCLE_LABELS } from '../types/timer'

const CYCLE_TYPES: CycleType[] = ['work', 'shortBreak', 'longBreak']

export default function CycleSelector({
  currentCycle,
  onSwitchCycle,
}: {
  currentCycle: CycleType
  onSwitchCycle: (cycle: CycleType) => void
}) {
  return (
    <div className="flex items-center gap-2">
      {CYCLE_TYPES.map((cycle) => (
        <button
          key={cycle}
          onClick={() => onSwitchCycle(cycle)}
          className={`rounded-full px-4 py-2 text-white text-sm transition-all duration-200 ${
            cycle === currentCycle
              ? 'bg-white/30 font-semibold shadow-lg'
              : 'bg-white/10 hover:bg-white/20'
          }`}
        >
          {CYCLE_LABELS[cycle]}
        </button>
      ))}
    </div>
  )
}

import { useState } from 'react'
import { NotificationSettings as NotificationSettingsType } from '../types/notifications'

function Toggle({ enabled, onToggle }: { enabled: boolean; onToggle: () => void }) {
  return (
    <button
      role="switch"
      aria-checked={enabled}
      onClick={onToggle}
      className={`relative inline-flex h-6 w-11 shrink-0 cursor-pointer rounded-full transition-colors duration-200 ${
        enabled ? 'bg-white/40' : 'bg-white/15'
      }`}
    >
      <span
        className={`pointer-events-none inline-block h-5 w-5 translate-y-0.5 rounded-full bg-white shadow-sm transition-transform duration-200 ${
          enabled ? 'translate-x-[22px]' : 'translate-x-0.5'
        }`}
      />
    </button>
  )
}

export default function NotificationSettings({
  settings,
  onUpdateSettings,
  onRequestPermission,
}: {
  settings: NotificationSettingsType
  onUpdateSettings: (settings: Partial<NotificationSettingsType>) => void
  onRequestPermission: () => Promise<boolean>
}) {
  const [isOpen, setIsOpen] = useState(false)

  const handleBrowserToggle = async () => {
    if (!settings.browserEnabled) {
      const granted = await onRequestPermission()
      if (granted) {
        onUpdateSettings({ browserEnabled: true })
      }
    } else {
      onUpdateSettings({ browserEnabled: false })
    }
  }

  const supportsNotifications = 'Notification' in window

  return (
    <div className="fixed top-4 right-4 z-40">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-2.5 rounded-xl bg-white/20 hover:bg-white/30 active:scale-95 transition-all duration-200 text-white"
        aria-label="Notification settings"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="currentColor"
          className="w-5 h-5"
        >
          <path
            fillRule="evenodd"
            d="M11.078 2.25c-.917 0-1.699.663-1.85 1.567L9.05 4.889c-.02.12-.115.26-.297.348a7.493 7.493 0 0 0-.986.57c-.166.115-.334.126-.45.083L6.3 5.508a1.875 1.875 0 0 0-2.282.819l-.922 1.597a1.875 1.875 0 0 0 .432 2.385l.84.692c.095.078.17.229.154.43a7.598 7.598 0 0 0 0 1.139c.015.2-.059.352-.153.43l-.841.692a1.875 1.875 0 0 0-.432 2.385l.922 1.597a1.875 1.875 0 0 0 2.282.818l1.019-.382c.115-.043.283-.031.45.082.312.214.641.405.985.57.182.088.277.228.297.35l.178 1.071c.151.904.933 1.567 1.85 1.567h1.844c.916 0 1.699-.663 1.85-1.567l.178-1.072c.02-.12.114-.26.297-.349.344-.165.673-.356.985-.57.167-.114.335-.125.45-.082l1.02.382a1.875 1.875 0 0 0 2.28-.819l.923-1.597a1.875 1.875 0 0 0-.432-2.385l-.84-.692c-.095-.078-.17-.229-.154-.43a7.614 7.614 0 0 0 0-1.139c-.016-.2.059-.352.153-.43l.84-.692c.708-.582.891-1.59.433-2.385l-.922-1.597a1.875 1.875 0 0 0-2.282-.818l-1.02.382c-.114.043-.282.031-.449-.083a7.49 7.49 0 0 0-.985-.57c-.183-.087-.277-.227-.297-.348l-.179-1.072a1.875 1.875 0 0 0-1.85-1.567h-1.843ZM12 15.75a3.75 3.75 0 1 0 0-7.5 3.75 3.75 0 0 0 0 7.5Z"
            clipRule="evenodd"
          />
        </svg>
      </button>

      {isOpen && (
        <div className="absolute top-full right-0 mt-2 w-64 bg-white/20 backdrop-blur-lg rounded-xl shadow-2xl p-4 text-white">
          <h3 className="text-sm font-semibold mb-3">Notifications</h3>

          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm">Sound notifications</span>
              <Toggle
                enabled={settings.audioEnabled}
                onToggle={() => onUpdateSettings({ audioEnabled: !settings.audioEnabled })}
              />
            </div>

            {supportsNotifications && (
              <div className="flex items-center justify-between">
                <span className="text-sm">Browser notifications</span>
                <Toggle enabled={settings.browserEnabled} onToggle={handleBrowserToggle} />
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

import { useState } from 'react'
import { TimePeriod, PERIOD_LABELS, StatisticsSummary, DailyFocusData } from '../types/statistics'
import StatisticsChart from './StatisticsChart'

function formatMinutesToDisplay(minutes: number): string {
  const totalMinutes = Math.round(minutes)
  if (totalMinutes < 60) return `${totalMinutes}m`
  const hours = Math.floor(totalMinutes / 60)
  const remaining = totalMinutes % 60
  return `${hours}h ${remaining}m`
}

const PERIODS: TimePeriod[] = ['today', 'last7days', 'last28days']

export default function StatisticsModal({
  isOpen,
  onClose,
  getStatistics,
  getDailyFocusData,
}: {
  isOpen: boolean
  onClose: () => void
  getStatistics: (period: TimePeriod) => StatisticsSummary
  getDailyFocusData: (period: TimePeriod) => DailyFocusData[]
}) {
  const [selectedPeriod, setSelectedPeriod] = useState<TimePeriod>('today')

  if (!isOpen) return null

  const stats = getStatistics(selectedPeriod)
  const dailyData = getDailyFocusData(selectedPeriod)

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center p-4 animate-modal-backdrop-enter"
      onClick={onClose}
    >
      <div className="absolute inset-0 bg-black/50" />
      <div
        className="relative w-full max-w-[480px] bg-white/20 backdrop-blur-lg rounded-2xl shadow-2xl p-6 animate-modal-enter"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="flex items-center justify-between mb-5">
          <h2 className="text-lg font-semibold text-white">Focus Statistics</h2>
          <button
            onClick={onClose}
            className="p-1 rounded-lg hover:bg-white/20 transition-colors text-white"
            aria-label="Close statistics"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              className="w-5 h-5"
            >
              <path d="M6.28 5.22a.75.75 0 0 0-1.06 1.06L8.94 10l-3.72 3.72a.75.75 0 1 0 1.06 1.06L10 11.06l3.72 3.72a.75.75 0 1 0 1.06-1.06L11.06 10l3.72-3.72a.75.75 0 0 0-1.06-1.06L10 8.94 6.28 5.22Z" />
            </svg>
          </button>
        </div>

        {/* Period Selector */}
        <div className="flex gap-2 mb-5">
          {PERIODS.map((period) => (
            <button
              key={period}
              onClick={() => setSelectedPeriod(period)}
              className={`rounded-full px-4 py-2 text-white text-sm transition-all duration-200 ${
                selectedPeriod === period
                  ? 'bg-white/30 font-semibold shadow-lg'
                  : 'bg-white/10 hover:bg-white/20'
              }`}
            >
              {PERIOD_LABELS[period]}
            </button>
          ))}
        </div>

        {/* Metric Cards */}
        <div className="grid grid-cols-3 gap-3 mb-5">
          <div className="bg-white/10 rounded-xl p-4 text-center">
            <div className="text-2xl font-bold text-white">
              {formatMinutesToDisplay(stats.totalFocusMinutes)}
            </div>
            <div className="text-xs text-white/70 mt-1">Focus Time</div>
          </div>
          <div className="bg-white/10 rounded-xl p-4 text-center">
            <div className="text-2xl font-bold text-white">{stats.focusSessions}</div>
            <div className="text-xs text-white/70 mt-1">Sessions</div>
          </div>
          <div className="bg-white/10 rounded-xl p-4 text-center">
            <div className="text-2xl font-bold text-white">
              {formatMinutesToDisplay(stats.totalBreakMinutes)}
            </div>
            <div className="text-xs text-white/70 mt-1">Break Time</div>
          </div>
        </div>

        {/* Bar Chart */}
        <StatisticsChart data={dailyData} period={selectedPeriod} />
      </div>
    </div>
  )
}

