## File Contents

### 1. **src/types/timer.ts**
```typescript
export type CycleType = 'work' | 'shortBreak' | 'longBreak';

export const CYCLE_DURATIONS: Record<CycleType, number> = {
  work: 1500,
  shortBreak: 300,
  longBreak: 900,
};

export const CYCLE_LABELS: Record<CycleType, string> = {
  work: 'Work',
  shortBreak: 'Short Break',
  longBreak: 'Long Break',
};

export interface TimerState {
  timeRemaining: number;
  isRunning: boolean;
  currentCycle: CycleType;
}

export interface TimerActions {
  start: () => void;
  pause: () => void;
  reset: () => void;
  switchCycle: (cycle: CycleType) => void;
}
```

### 2. **src/hooks/useTimer.ts**
```typescript
import { useState, useEffect, useCallback, useRef } from 'react';
import { CycleType, CYCLE_DURATIONS, TimerState, TimerActions } from '../types/timer';

export function useTimer(): TimerState & TimerActions {
  const [timeRemaining, setTimeRemaining] = useState(CYCLE_DURATIONS.work);
  const [isRunning, setIsRunning] = useState(false);
  const [currentCycle, setCurrentCycle] = useState<CycleType>('work');
  const intervalRef = useRef<number | null>(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = window.setInterval(() => {
        setTimeRemaining((prev) => {
          if (prev <= 1) {
            setIsRunning(false);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }

    return () => {
      if (intervalRef.current !== null) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, [isRunning]);

  const start = useCallback(() => {
    if (timeRemaining > 0) {
      setIsRunning(true);
    }
  }, [timeRemaining]);

  const pause = useCallback(() => {
    setIsRunning(false);
  }, []);

  const reset = useCallback(() => {
    setIsRunning(false);
    setTimeRemaining(CYCLE_DURATIONS[currentCycle]);
  }, [currentCycle]);

  const switchCycle = useCallback((cycle: CycleType) => {
    setIsRunning(false);
    setCurrentCycle(cycle);
    setTimeRemaining(CYCLE_DURATIONS[cycle]);
  }, []);

  return {
    timeRemaining,
    isRunning,
    currentCycle,
    start,
    pause,
    reset,
    switchCycle,
  };
}
```

### 3. **src/components/PomodoroTimer.tsx**
```typescript
import { useTimer } from '../hooks/useTimer';
import { CycleType } from '../types/timer';
import TimerDisplay from './TimerDisplay';
import TimerControls from './TimerControls';
import CycleSelector from './CycleSelector';

const CYCLE_COLORS: Record<CycleType, string> = {
  work: 'from-rose-500 to-red-600',
  shortBreak: 'from-emerald-500 to-green-600',
  longBreak: 'from-sky-500 to-blue-600',
};

export default function PomodoroTimer() {
  const { timeRemaining, isRunning, currentCycle, start, pause, reset, switchCycle } = useTimer();

  return (
    <div
      className={`min-h-screen bg-gradient-to-br ${CYCLE_COLORS[currentCycle]} transition-all duration-700 flex flex-col items-center justify-center p-4`}
    >
      <p className="text-white/70 text-sm font-medium tracking-widest uppercase mb-8">
        Pomodoro Timer
      </p>
      <CycleSelector currentCycle={currentCycle} onSwitchCycle={switchCycle} />
      <div className="my-12">
        <TimerDisplay timeRemaining={timeRemaining} isRunning={isRunning} />
      </div>
      <TimerControls isRunning={isRunning} onStart={start} onPause={pause} onReset={reset} />
    </div>
  );
}
```

### 4. **src/components/TimerDisplay.tsx**
```typescript
function formatTime(seconds: number): string {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
}

export default function TimerDisplay({
  timeRemaining,
  isRunning,
}: {
  timeRemaining: number;
  isRunning: boolean;
}) {
  return (
    <div
      className={`text-8xl sm:text-9xl font-mono font-bold tracking-tight text-white ${
        isRunning ? 'animate-pulse-slow' : ''
      }`}
    >
      {formatTime(timeRemaining)}
    </div>
  );
}
```

