  1→import { useState, useCallback, useEffect, useRef } from 'react'
  2→import { CycleType } from '../types/timer'
  3→import {
  4→  NotificationSettings,
  5→  NotificationState,
  6→  COMPLETION_MESSAGES,
  7→  NEXT_CYCLE_SUGGESTIONS,
  8→} from '../types/notifications'
  9→import { playNotificationSound } from '../utils/audio'
 10→
 11→const STORAGE_KEY = 'pomodoro-notification-settings'
 12→const AUTO_DISMISS_MS = 5000
 13→
 14→function loadSettings(): NotificationSettings {
 15→  try {
 16→    const stored = localStorage.getItem(STORAGE_KEY)
 17→    if (stored) {
 18→      const parsed = JSON.parse(stored)
 19→      return {
 20→        audioEnabled: typeof parsed.audioEnabled === 'boolean' ? parsed.audioEnabled : true,
 21→        browserEnabled: typeof parsed.browserEnabled === 'boolean' ? parsed.browserEnabled : false,
 22→      }
 23→    }
 24→  } catch {
 25→    // Corrupted localStorage — fall back to defaults
 26→  }
 27→  return { audioEnabled: true, browserEnabled: false }
 28→}
 29→
 30→function saveSettings(settings: NotificationSettings) {
 31→  try {
 32→    localStorage.setItem(STORAGE_KEY, JSON.stringify(settings))
 33→  } catch {
 34→    // localStorage full or unavailable
 35→  }
 36→}
 37→
 38→const INITIAL_NOTIFICATION_STATE: NotificationState = {
 39→  isVisible: false,
 40→  message: '',
 41→  suggestion: '',
 42→  cycleType: null,
 43→}
 44→
 45→export function useNotifications() {
 46→  const [settings, setSettings] = useState<NotificationSettings>(loadSettings)
 47→  const [notificationState, setNotificationState] = useState<NotificationState>(
 48→    INITIAL_NOTIFICATION_STATE
 49→  )
 50→  const dismissTimerRef = useRef<number | null>(null)
 51→
 52→  useEffect(() => {
 53→    saveSettings(settings)
 54→  }, [settings])
 55→
 56→  const clearDismissTimer = useCallback(() => {
 57→    if (dismissTimerRef.current !== null) {
 58→      clearTimeout(dismissTimerRef.current)
 59→      dismissTimerRef.current = null
 60→    }
 61→  }, [])
 62→
 63→  const dismissNotification = useCallback(() => {
 64→    clearDismissTimer()
 65→    setNotificationState(INITIAL_NOTIFICATION_STATE)
 66→  }, [clearDismissTimer])
 67→
 68→  const triggerNotification = useCallback(
 69→    (cycleType: CycleType) => {
 70→      clearDismissTimer()
 71→
 72→      setNotificationState({
 73→        isVisible: true,
 74→        message: COMPLETION_MESSAGES[cycleType],
 75→        suggestion: NEXT_CYCLE_SUGGESTIONS[cycleType],
 76→        cycleType,
 77→      })
 78→
 79→      if (settings.audioEnabled) {
 80→        playNotificationSound(cycleType)
 81→      }
 82→
 83→      if (settings.browserEnabled && !document.hasFocus() && 'Notification' in window) {
 84→        if (Notification.permission === 'granted') {
 85→          new Notification(COMPLETION_MESSAGES[cycleType], {
 86→            body: NEXT_CYCLE_SUGGESTIONS[cycleType],
 87→          })
 88→        }
 89→      }
 90→
 91→      dismissTimerRef.current = window.setTimeout(() => {
 92→        setNotificationState(INITIAL_NOTIFICATION_STATE)
 93→        dismissTimerRef.current = null
 94→      }, AUTO_DISMISS_MS)
 95→    },
 96→    [settings.audioEnabled, settings.browserEnabled, clearDismissTimer]
 97→  )
 98→
 99→  const updateSettings = useCallback((partial: Partial<NotificationSettings>) => {
100→    setSettings((prev) => ({ ...prev, ...partial }))
101→  }, [])
102→
103→  const requestBrowserPermission = useCallback(async (): Promise<boolean> => {
104→    if (!('Notification' in window)) {
105→      return false
106→    }
107→    if (Notification.permission === 'granted') {
108→      return true
109→    }
110→    if (Notification.permission === 'denied') {
111→      return false
112→    }
113→    const result = await Notification.requestPermission()
114→    return result === 'granted'
115→  }, [])
116→
117→  useEffect(() => {
118→    return () => {
119→      clearDismissTimer()
120→    }
121→  }, [clearDismissTimer])
122→
123→  return {
124→    settings,
125→    updateSettings,
126→    notificationState,
127→    triggerNotification,
128→    dismissNotification,
129→    requestBrowserPermission,
130→  }
131→}
132→

