import { useState, useEffect, useCallback, useRef } from 'react'
import { CycleType, CYCLE_DURATIONS, TimerState, TimerActions } from '../types/timer'

export function useTimer(onComplete?: (cycleType: CycleType) => void): TimerState & TimerActions {
  const [timeRemaining, setTimeRemaining] = useState(CYCLE_DURATIONS.work)
  const [isRunning, setIsRunning] = useState(false)
  const [currentCycle, setCurrentCycle] = useState<CycleType>('work')
  const intervalRef = useRef<number | null>(null)
  const onCompleteRef = useRef(onComplete)
  const currentCycleRef = useRef(currentCycle)

  onCompleteRef.current = onComplete
  currentCycleRef.current = currentCycle

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = window.setInterval(() => {
        setTimeRemaining((prev) => {
          if (prev <= 1) {
            setIsRunning(false)
            onCompleteRef.current?.(currentCycleRef.current)
            return 0
          }
          return prev - 1
        })
      }, 1000)
    }

    return () => {
      if (intervalRef.current !== null) {
        clearInterval(intervalRef.current)
        intervalRef.current = null
      }
    }
  }, [isRunning])

  const start = useCallback(() => {
    if (timeRemaining > 0) {
      setIsRunning(true)
    }
  }, [timeRemaining])

  const pause = useCallback(() => {
    setIsRunning(false)
  }, [])

  const reset = useCallback(() => {
    setIsRunning(false)
    setTimeRemaining(CYCLE_DURATIONS[currentCycle])
  }, [currentCycle])

  const switchCycle = useCallback((cycle: CycleType) => {
    setIsRunning(false)
    setCurrentCycle(cycle)
    setTimeRemaining(CYCLE_DURATIONS[cycle])
  }, [])

  return {
    timeRemaining,
    isRunning,
    currentCycle,
    start,
    pause,
    reset,
    switchCycle,
  }
}

import { useTimer } from '../hooks/useTimer'
import { useNotifications } from '../hooks/useNotifications'
import { CycleType } from '../types/timer'
import TimerDisplay from './TimerDisplay'
import TimerControls from './TimerControls'
import CycleSelector from './CycleSelector'
import NotificationBanner from './NotificationBanner'
import NotificationSettings from './NotificationSettings'

const CYCLE_COLORS: Record<CycleType, string> = {
  work: 'from-rose-500 to-red-600',
  shortBreak: 'from-emerald-500 to-green-600',
  longBreak: 'from-sky-500 to-blue-600',
}

export default function PomodoroTimer() {
  const {
    settings,
    updateSettings,
    notificationState,
    triggerNotification,
    dismissNotification,
    requestBrowserPermission,
  } = useNotifications()

  const { timeRemaining, isRunning, currentCycle, start, pause, reset, switchCycle } =
    useTimer(triggerNotification)

  return (
    <div
      className={`min-h-screen bg-gradient-to-br ${CYCLE_COLORS[currentCycle]} transition-all duration-700 flex flex-col items-center justify-center p-4`}
    >
      <NotificationBanner
        message={notificationState.message}
        suggestion={notificationState.suggestion}
        cycleType={notificationState.cycleType}
        isVisible={notificationState.isVisible}
        onDismiss={dismissNotification}
      />
      <NotificationSettings
        settings={settings}
        onUpdateSettings={updateSettings}
        onRequestPermission={requestBrowserPermission}
      />
      <p className="text-white/70 text-sm font-medium tracking-widest uppercase mb-8">
        Pomodoro Timer
      </p>
      <CycleSelector currentCycle={currentCycle} onSwitchCycle={switchCycle} />
      <div className="my-12">
        <TimerDisplay timeRemaining={timeRemaining} isRunning={isRunning} />
      </div>
      <TimerControls isRunning={isRunning} onStart={start} onPause={pause} onReset={reset} />
    </div>
  )
}

/** @type {import('tailwindcss').Config} */
export default {
  content: [
    "./index.html",
    "./src/**/*.{js,ts,jsx,tsx}",
  ],
  theme: {
    extend: {
      animation: {
        'pulse-slow': 'pulse-slow 2s ease-in-out infinite',
        'notification-enter': 'notification-enter 0.3s ease-out',
        'notification-exit': 'notification-exit 0.3s ease-in',
      },
      keyframes: {
        'pulse-slow': {
          '0%, 100%': { opacity: '1' },
          '50%': { opacity: '0.7' },
        },
        'notification-enter': {
          '0%': { opacity: '0', transform: 'translate(-50%, -100%)' },
          '100%': { opacity: '1', transform: 'translate(-50%, 0)' },
        },
        'notification-exit': {
          '0%': { opacity: '1', transform: 'translate(-50%, 0)' },
          '100%': { opacity: '0', transform: 'translate(-50%, -100%)' },
        },
      },
    },
  },
  plugins: [],
}

import { useState } from 'react'
import { NotificationSettings as NotificationSettingsType } from '../types/notifications'

function Toggle({ enabled, onToggle }: { enabled: boolean; onToggle: () => void }) {
  return (
    <button
      role="switch"
      aria-checked={enabled}
      onClick={onToggle}
      className={`relative inline-flex h-6 w-11 shrink-0 cursor-pointer rounded-full transition-colors duration-200 ${
        enabled ? 'bg-white/40' : 'bg-white/15'
      }`}
    >
      <span
        className={`pointer-events-none inline-block h-5 w-5 translate-y-0.5 rounded-full bg-white shadow-sm transition-transform duration-200 ${
          enabled ? 'translate-x-[22px]' : 'translate-x-0.5'
        }`}
      />
    </button>
  )
}

export default function NotificationSettings({
  settings,
  onUpdateSettings,
  onRequestPermission,
}: {
  settings: NotificationSettingsType
  onUpdateSettings: (settings: Partial<NotificationSettingsType>) => void
  onRequestPermission: () => Promise<boolean>
}) {
  const [isOpen, setIsOpen] = useState(false)

  const handleBrowserToggle = async () => {
    if (!settings.browserEnabled) {
      const granted = await onRequestPermission()
      if (granted) {
        onUpdateSettings({ browserEnabled: true })
      }
    } else {
      onUpdateSettings({ browserEnabled: false })
    }
  }

  const supportsNotifications = 'Notification' in window

  return (
    <div className="fixed top-4 right-4 z-40">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-2.5 rounded-xl bg-white/20 hover:bg-white/30 active:scale-95 transition-all duration-200 text-white"
        aria-label="Notification settings"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          fill="currentColor"
          className="w-5 h-5"
        >
          <path
            fillRule="evenodd"
            d="M11.078 2.25c-.917 0-1.699.663-1.85 1.567L9.05 4.889c-.02.12-.115.26-.297.348a7.493 7.493 0 0 0-.986.57c-.166.115-.334.126-.45.083L6.3 5.508a1.875 1.875 0 0 0-2.282.819l-.922 1.597a1.875 1.875 0 0 0 .432 2.385l.84.692c.095.078.17.229.154.43a7.598 7.598 0 0 0 0 1.139c.015.2-.059.352-.153.43l-.841.692a1.875 1.875 0 0 0-.432 2.385l.922 1.597a1.875 1.875 0 0 0 2.282.818l1.019-.382c.115-.043.283-.031.45.082.312.214.641.405.985.57.182.088.277.228.297.35l.178 1.071c.151.904.933 1.567 1.85 1.567h1.844c.916 0 1.699-.663 1.85-1.567l.178-1.072c.02-.12.114-.26.297-.349.344-.165.673-.356.985-.57.167-.114.335-.125.45-.082l1.02.382a1.875 1.875 0 0 0 2.28-.819l.923-1.597a1.875 1.875 0 0 0-.432-2.385l-.84-.692c-.095-.078-.17-.229-.154-.43a7.614 7.614 0 0 0 0-1.139c-.016-.2.059-.352.153-.43l.84-.692c.708-.582.891-1.59.433-2.385l-.922-1.597a1.875 1.875 0 0 0-2.282-.818l-1.02.382c-.114.043-.282.031-.449-.083a7.49 7.49 0 0 0-.985-.57c-.183-.087-.277-.227-.297-.348l-.179-1.072a1.875 1.875 0 0 0-1.85-1.567h-1.843ZM12 15.75a3.75 3.75 0 1 0 0-7.5 3.75 3.75 0 0 0 0 7.5Z"
            clipRule="evenodd"
          />
        </svg>
      </button>

      {isOpen && (
        <div className="absolute top-full right-0 mt-2 w-64 bg-white/20 backdrop-blur-lg rounded-xl shadow-2xl p-4 text-white">
          <h3 className="text-sm font-semibold mb-3">Notifications</h3>

          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm">Sound notifications</span>
              <Toggle
                enabled={settings.audioEnabled}
                onToggle={() => onUpdateSettings({ audioEnabled: !settings.audioEnabled })}
              />
            </div>

            {supportsNotifications && (
              <div className="flex items-center justify-between">
                <span className="text-sm">Browser notifications</span>
                <Toggle enabled={settings.browserEnabled} onToggle={handleBrowserToggle} />
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

